var grabUserIdAndCacheCredentials = function(oauth2Client, successCallback) {
    ctx.googleapis
    .discover('plus', 'v1')
    .execute(function(err, client) {
		if(!!err) { 
			console.error(err);
			msg.res.send("401", {"errorMessage" : err}); 
			return;
		}
		client
		    .plus.people.get({ userId: 'me' })
		    .withAuthClient(oauth2Client)
		    .execute(function(err, res) {	
		    	if(!!err) { 				
					console.error(err);
					msg.res.send("401", {"errorMessage" : err}); 
					return;
				}
				msg.payload = res;
				cb(msg);
				successCallback(res.id, res.displayName, oauth2Client);			
			});		
    });   
};

var grabToken = function (code, oauth2Client, errorCallback, successCallback) {
    oauth2Client.getToken(code, function (err, tokens) {
        if (!!err) {
            errorCallback(err);
        } else {
            console.log('tokens', tokens);
            oauth2Client.setCredentials(tokens);
            grabUserIdAndCacheCredentials(oauth2Client, successCallback);
        }
    });
};

